import React, {useEffect, useState} from "react";
import {Product} from "../interfaces/product";
import {Link} from "react-router-dom";

const Products = () => {
  const [products, setProducts] = useState ([]);

  useEffect( () => {
    (
      async () => {
        const response = await fetch('http://localhost:8000/api/products');
        const data = await response.json();

        setProducts(data);
      }
    )();
  },[]);

  const del = async (id: number) =>{
    if(window.confirm('Confirm deleting this product?')){
      await fetch(`http://localhost:8000/api/products/${id}`, {method: 'DELETE'
      });
      setProducts(products.filter((p: Product) => p.id !== id));
    }
  }

    return (

      <><Link to={'/products/create'} className="btn btn-sm btn-outline-secondary"> ADD </Link><div className="table-responsive">
        <table className="table table-striped table-sm">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Image</th>
              <th scope="col">Title</th>
              <th scope="col">Likes</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {products.map(
              (p: Product) => {
                return (
                  <tr key={p.id}>
                    <td>{p.id}</td>
                    <td><img src={p.image} height="100" /></td>
                    <td>{p.title}</td>
                    <td>{p.likes}</td>
                    <td>
                      <div className="btn-group mr-2">
                      <Link to={`/products/${p.id}/edit`} className="btn btn-sm btn-outline-secondary">Edit</Link>
                      <a href="#" className="btn btn-sm btn-outline-secondary"
                          onClick={() => del(p.id)}
                        >Delete</a>
                      </div>
                    </td>
                  </tr>
                );

              })}

          </tbody>
        </table>
      </div></>
    );
};

export default Products;
